import React, { useEffect, useState } from "react";
import "../../App.css";
import Loader from "../layout/Loader";
import { useDispatch, useSelector } from "react-redux";
import {
  getExperienceDetails,
  updateExperience,
  getExperiences,
} from "../../JS/actions/index";
import {
  Button,
  Card,
  CardHeader,
  CardBody,
  Col,
  Media,
  Row,
  Modal,
  ModalHeader,
  ModalBody,
  ModalFooter,
  Alert,
} from "reactstrap";
import { Link } from "react-router-dom";
import Carrousel from "../layout/Carrousel";
import AuthNavbar from "../layout/AuthNavbar";

const ExperienceDetails = ({
  match: {
    params: { id },
  },
}) => {
  const dispatch = useDispatch();
  const [modal, setModal] = useState(false);
  const toggle = () => setModal(!modal);
  useEffect(() => {
    console.log(`object`);
    dispatch(getExperienceDetails(id));
  }, [dispatch, id]);

  const isLoading = useSelector((state) => state.experiencesReducers.isLoading);
  const experience = useSelector(
    (state) => state.experiencesReducers.experience
  );

  console.log(
    "ðŸš€ ~ file: ExperienceDetails.js ~ line 38 ~ ExperienceDetails ~ experience.langue",
    experience
  );
  return isLoading ? (
    <Loader />
  ) : experience ? (
    <>
      {/* Modal */}
      <Modal isOpen={modal} toggle={toggle}>
        <ModalHeader toggle={toggle}>Demander la validation?</ModalHeader>
